import { Buffer } from 'buffer';

import { WalletContractV4 as WalletContractV4R2 } from '@ton/ton/dist/wallets/WalletContractV4';
import { Cell, contractAddress } from '@ton/core';
import {
    WalletContractV1R1,
    WalletContractV1R2,
    WalletContractV1R3,
    WalletContractV2R1,
    WalletContractV2R2,
    WalletContractV3R1,
    WalletContractV3R2,
    WalletContractV5R1,
} from '@ton/ton';

export type WalletVersion =
    | 'v1r1'
    | 'v1r2'
    | 'v1r3'
    | 'v2r1'
    | 'v2r2'
    | 'v3r1'
    | 'v3r2'
    | 'v4'
    | 'v4r1'
    | 'v4r2'
    | 'v5r1';

class WalletContractV4R1 {
    static create(args: { workchain: number; publicKey: Buffer; walletId?: number | null }) {
        const wallet = WalletContractV4R2.create(args);
        const { data } = wallet.init;
        const code = Cell.fromBoc(
            Buffer.from(
                '',
                'hex',
            ),
        )[0]!;
        (wallet as any).init = { data, code };
        (wallet as any).address = contractAddress(args.workchain, wallet.init);
        return wallet;
    }
}

export const wallets = {
    v1r1: WalletContractV1R1,
    v1r2: WalletContractV1R2,
    v1r3: WalletContractV1R3,
    v2r1: WalletContractV2R1,
    v2r2: WalletContractV2R2,
    v3r1: WalletContractV3R1,
    v3r2: WalletContractV3R2,
    v4: WalletContractV4R2, // left for backward compatibility
    v4r1: WalletContractV4R1,
    v4r2: WalletContractV4R2,
    v5r1: WalletContractV5R1,
};
